import React, { useState } from 'react';
import Button from './Button.jsx'
import Web from './Web.jsx'

function Portfolio(){
    const [portfolioView, setPortfolioView] = useState(null)

    function handleChoice(choice){
        setPortfolioView(choice)
    }

    function choiceStatus(){
        if (portfolioView === null) {
            return (
                <div className="choiceParent">
                   <Button name="Web Development" color="white" onClick={handleChoice("Web")} />
                    <Button name="Graphic Design" color="white" onClick={handleChoice("Graphic")} />
                </div>     
            )
        } else {
            return (
            <div>
                <Button name="Web Development" color="white" onClick={handleChoice("Web")} />
                <Button name="Graphic Design" color="white" onClick={handleChoice("Graphic")} />
            </div>     
            )
        }
    }

    function renderPortfolio(){
        if (portfolioView === "Web"){
            return <Web />
        }
    }

    return (
        <div>
            <h1>Portfolio</h1>
            { choiceStatus() }
            { renderPortfolio() }
        </div>
    )
}

export default Portfolio
