import React, { useState } from 'react';
import './App.css';
import Header from './components/Header.jsx'
import Choice from './components/Choice.jsx'
import Languages from './components/Languages.jsx'
import Skills from './components/Skills.jsx'
import Portfolio from './components/Portfolio.jsx'
import Button from './components/Button.jsx'

function App() {
  const [navigation, setNavigation] = useState(null)

  function chooseSkills(skills){
    console.log(skills)
    setNavigation(skills)
  }

  function renderChoice(){
      if (navigation === null){
        return <Header 
                  onChoice={chooseSkills}
                  />
        } if (navigation === "Main menu"){
          return <Choice 
                    onChoice={chooseSkills}
                 />
        } if (navigation === "Skills") {
          return <Skills />
        } if (navigation === "Portfolio") {
          return <Portfolio />
        } if (navigation === "Languages") {
          return <Languages />
        }
    }

  return (
    <div className="mainpage">
      <Button 
          name="Main menu"
          color="white"
          onChoice={chooseSkills}
          />
     { renderChoice() }
      <h2>Fullstack Web Developer and Graphic Designer</h2>
    </div>
  );
}

export default App;
